import { Card, CardBody, CardTitle, CardSubtitle, Table } from "reactstrap";
import React, {useState, useEffect } from 'react'
import { useParams,Link, useNavigate } from 'react-router-dom'
 
const ProjectTables = () => {


  const [userName, setuserName] = React.useState('');
  const [email, setEmail] = React.useState('');
  const [role, setRole] = React.useState('');
  const [userList, setuserList] = React.useState('');
  
  const [classList, setclassList] = React.useState('');
  
  
  
  const params = useParams();
  const navigate = useNavigate()
  const roleLogin = localStorage.getItem('role')
  
  const auth = localStorage.getItem('user')
  const token = JSON.parse(auth).data;
  useEffect(()=>{
  getUserDetails();
  getListUser();
  getClassRoom();
  },[])
  
  const getUserDetails = async () => {
  try {
   const result = await fetch('http://localhost:3000/authen/me', {
     method: 'GET',
     headers: {
       'Content-Type': 'application/json',
       Authorization: `Bearer ${token}`, 
     },
   });
  
   if (result.ok) {
      
     const data = await result.json();
       
     setEmail(data.data.email);
     setuserName(data.data.userName);
     setRole(data.data.role);
     localStorage.setItem('role', JSON.stringify(data.data.role)); 
  
   } else {
     alert('Failed to get profile');
   }
  } catch (error) {
   console.error('Error:', error);
   alert('An error occurred');
  }
  };
  const getListUser = async () => {
    try {
      const result = await fetch('http://localhost:3000/users', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          // Authorization: `Bearer ${token}`, 
        },
      });
  
      if (result.ok) {
         
        const data = await result.json();
         
        setuserList(data.data);
         
  
      } else {
        alert('Failed to get userList');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred');
    }
  };

  if (roleLogin==='"admin"') {
    return (
      <div>
       

        <div className="product-list">
            {/* <a> Welcome {JSON.parse(userName).data.userName} <p>ROLE: {JSON.parse(userName).data.role}</p></a> */}

      <h1>User List</h1>
      <div>
      <Card>
        <CardBody>
          <CardTitle tag="h5">Project Listing</CardTitle>
          <CardSubtitle className="mb-2 text-muted" tag="h6">
            Overview of the projects
          </CardSubtitle>

          <Table className="no-wrap mt-3 align-middle" responsive borderless>
            <thead>
              <tr>
                <th>No.</th>
                <th>username</th>

                <th>role</th>
                <th>Options</th>
                 
              </tr>
            </thead>
            <tbody>
            {userList.length > 0 ? (
        userList.map((item, index) => (
                <tr key={item._id} className="border-top">
                  <td>
                    <div className="d-flex align-items-center p-2">
                      
                      <div className="ms-3">
                        <h5 className="mb-0">{index +1}</h5>
                
                      </div>
                    </div>
                  </td>
                  <td>{item.userName}</td>
                  <td>
             
                  </td>
                  <td>{item.role}</td>
                  <td> 
                  <button onClick={() => deleteUser(item._id)}>Delete</button>
        <Link to={`/users/${item._id}`}>Update</Link>
                  </td>
                </tr>
              ))) : (
                <h1>No Result Found</h1>
              )}
            </tbody>
          </Table>
        </CardBody>
      </Card>
    </div>

    </div>
      </div>


    );
  }
  return (
    <div>
    helo
    </div>
  );
};

export default ProjectTables;
