import { Button, Nav, NavItem } from "reactstrap";
import { Link, useLocation } from "react-router-dom";
import user1 from "../assets/images/users/user4.jpg";
import probg from "../assets/images/bg/download.jpg";
const roleLogin = localStorage.getItem('role')

const navigation = [
  {
    title: "Dashboard",
    href: "/starter",
    icon: "bi bi-speedometer2",
    allowedRoles: ["admin"],
  } 
  ,
  {
    title: "Table",
    href: "/table",
    icon: "bi bi-layout-split",
    allowedRoles: ["admin", "publisher"],
  },
  {
    title: "Forms",
    href: "/forms",
    icon: "bi bi-textarea-resize",
    allowedRoles: ["admin"],
  } 
];

const Sidebar = () => {
  const showMobilemenu = () => {
    document.getElementById("sidebarArea").classList.toggle("showSidebar");
  };
  let location = useLocation();

  return (
    <div>
      <div className="d-flex align-items-center"></div>
      <div
        className="profilebg"
        style={{ background: `url(${probg}) no-repeat` }}
      >
        <div className="p-3 d-flex">
          <img src={user1} alt="user" width="50" className="rounded-circle" />
          <Button
            color="white"
            className="ms-auto text-white d-lg-none"
            onClick={() => showMobilemenu()}
          >
            <i className="bi bi-x"></i>
          </Button>
        </div>
        <div className="bg-dark text-white p-2 opacity-75">Steave Rojer</div>
      </div>
      <div className="p-3 mt-2">
        <Nav vertical className="sidebarNav">
          {navigation.map((navi, index) => {
            // Kiểm tra xem vai trò hiện tại có trong danh sách vai trò được phép truy cập hay không
            if (navi.allowedRoles.includes(roleLogin==='"admin"')) {
              return (
                <NavItem key={index} className="sidenav-bg">
                  <Link
                    to={navi.href}
                    className={
                      location.pathname === navi.href
                        ? "active nav-link py-3"
                        : "nav-link text-secondary py-3"
                    }
                  >
                    <i className={navi.icon}></i>
                    <span className="ms-3 d-inline-block">{navi.title}</span>
                  </Link>
                </NavItem>
              );
            } else {
              return null;
            }
          })}
        </Nav>
      </div>
    </div>
  );
};

export default Sidebar;
