import {
    Card,
    Row,
    Col,
    CardTitle,
    CardBody,
    Button,
    Form,
    FormGroup,
    Label,
    Input,
    FormText,
  } from "reactstrap";
  import React,{useEffect, useState} from 'react'
   import {useParams, useNavigate} from 'react-router-dom'

 
const UpdateStudent = () => {
    const [name, setName] = React.useState('');
    const [age, setAge] = React.useState('');
    const [address, settaddress] = React.useState('');
    const [gender, setGender] = React.useState('');

    const idClass = localStorage.getItem('idClass')

    const params = useParams();
    const navigate = useNavigate()

    useEffect(()=>{
        getUserDetails();
    },[])

    const getUserDetails = async ()=>{
        console.log(params)
        let result = await fetch(`http://localhost:3000/students/${params.id}`)
        result = await result.json()
        const user = result.data;  

        setName(user.name);
       
        setAge(user.age);
        settaddress(user.address);
         
    }

    const updateUsers = async () => {
        try {
          
          let result = await fetch(`http://localhost:3000/students/edit/${params.id}`, {
            method: 'Put',
            body: JSON.stringify({ name, age,address ,gender}),
            headers: {
              'Content-Type': 'application/json'
            }
          });
          result = await result.json();
          if (result.sucess===true) {
            navigate('/class/detailClass/'+idClass);
            localStorage.removeItem('idClass')
          }
          else{
            alert(result.data);
          }
        } catch (error) {
          
          console.log('Lỗi:', error.message);
          
        }
      };
    return (
      <Row>
        <Col>
          
          <Card>
            <CardTitle tag="h6" className="border-bottom p-3 mb-0">
              <i className="bi bi-bell me-2"> </i>
             Update User
            </CardTitle>
            <CardBody>
              <Form>
               
                <FormGroup>
                  <Label for="exampleEmail">Student name</Label>
                  <Input
                    id="exampleEmail"
                    name="email"
                    placeholder="with a placeholder"
                    type="text"
                    value={name}   onChange={(e)=> setName(e.target.value)}
                  />
                </FormGroup>
                <FormGroup>
                  <Label for="exampleEmail">Age</Label>
                  <Input
                    id="exampleEmail"
                    name="email"
                    placeholder="with a placeholder"
                    type="text"
                    value={age}   onChange={(e)=> setAge(e.target.value)} />
 
                </FormGroup>
                <FormGroup>
                  <Label for="exampleEmail">Address</Label>
                  <Input
                    id="exampleEmail"
                    name="email"
                    placeholder="with a placeholder"
                    type="text"
                    value={address}   onChange={(e)=> settaddress(e.target.value)} />

                </FormGroup>
                <FormGroup>
  <Label for="gender">Gender</Label>
  <div>
    <Label>
      <Input
        type="radio"
        name="gender"
        value="Male"
        checked={gender==='Male'}
        onChange={(e) => setGender(e.target.value)}
      />
      Male
    </Label>
  
    <Label>
      <Input
        type="radio"
        name="gender"
        value="Female"
        checked={gender==='Female'}style={{marginLeft:'5px;'}}
        onChange={(e) => setGender(e.target.value)}
      />
      Female
    </Label>
  </div>
</FormGroup>
           
                <button onClick={updateUsers} className="btn btn-primary" style={{ color: 'white', textDecoration: 'underline', marginLeft: '5px' }}type='button'>Update student</button>

              </Form>
            </CardBody>
          </Card>
          <table style={{ border: '1px solid black', width: '100%' }}>
      <thead>
        <tr>
          <th style={{ border: '1px solid black', padding: '8px' }}>Họ và tên</th>
          <th style={{ border: '1px solid black', padding: '8px' }}>Mã học sinh</th>
          <th style={{ border: '1px solid black', padding: '8px' }}>Lớp</th>
          <th style={{ border: '1px solid black', padding: '8px' }}>Môn học</th>
          <th style={{ border: '1px solid black', padding: '8px' }}>Điểm kiểm tra</th>
          <th style={{ border: '1px solid black', padding: '8px' }}>Điểm giữa kỳ</th>
          <th style={{ border: '1px solid black', padding: '8px' }}>Điểm cuối kỳ</th>
          <th style={{ border: '1px solid black', padding: '8px' }}>Điểm trung bình</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td style={{ border: '1px solid black', padding: '8px' }}>Nguyễn Văn A</td>
          <td style={{ border: '1px solid black', padding: '8px' }}>HS001</td>
          <td style={{ border: '1px solid black', padding: '8px' }}>3A</td>
          <td style={{ border: '1px solid black', padding: '8px' }}>Toán</td>
          <td style={{ border: '1px solid black', padding: '8px' }}>8.5</td>
          <td style={{ border: '1px solid black', padding: '8px' }}>9.0</td>
          <td style={{ border: '1px solid black', padding: '8px' }}>8.7</td>
          <td style={{ border: '1px solid black', padding: '8px' }}>8.7</td>
        </tr>
        {/* Các hàng dữ liệu khác */}
      </tbody>
    </table>
        </Col>
      </Row>
    );
  };
  
  export default UpdateStudent;
