import {
    Card,
    Row,
    Col,
    CardTitle,
    CardBody,
    Button,
    Form,
    FormGroup,
    Label,
    Input,
    FormText,
  } from "reactstrap";
  import React,{useEffect, useRef, useState} from 'react'
  import { useNavigate } from 'react-router-dom';

 
const AddStudent = () => {
    const navigate = useNavigate();
    const roleLogin = localStorage.getItem('role')

    const [name, setName] = React.useState('');
    const [age, setAge] = React.useState('');
    const [image, setImage] = React.useState('');
    const [gender, setGender] = React.useState('');

    const [address, settaddress] = React.useState('');
   
    const [error, setError] = React.useState(false);
    const [files, setFiles] = React.useState(null);
    const [previews, setPreviews] = useState();
    useEffect(() => {
      if (!files) return;
      let tmp = [];
      for (let i = 0; i < files.length; i++) {
        tmp.push(URL.createObjectURL(files[i]));
      }
      const objectUrls = tmp;
      setPreviews(objectUrls);
  
      // free memory
      for (let i = 0; i < objectUrls.length; i++) {
        return () => {
          URL.revokeObjectURL(objectUrls[i]);
        };
      }
    }, [files]);
  
    const auth = localStorage.getItem('user')
    const idClass = localStorage.getItem('idClass')
     const token = JSON.parse(auth).data;

     const formData = new FormData();
     formData.append('name', name);
     formData.append('age', age);
     formData.append('address', address);
     formData.append('gender', gender);
     if (image) {
       for (let i = 0; i < image.length; i++) {
         formData.append('image', image[i]);
       }
     }
     formData.append('class_k', idClass);

    const AddStudentToClass = async ()=>{
        if(!name  ||!age ||!address ||!gender  ){
             setError(true)
            return false
        }
           let response = await fetch('http://localhost:3000/students/add',{
            method:'post',
            body: formData,         
            headers:{
                'Content-Type': 'application/json',
                Authorization: `Bearer ${token}`, 
            }
        })

        if (response.ok) {
            const result = await response.json();
            console.log(result);
            
             navigate('/class/detailClass/'+idClass);
             localStorage.removeItem('idClass')
          } else {
            const result = await response.json();
            const errorMessage = result.data;
            console.error('Error:', response.status);
            alert(errorMessage);
            const data = await result.json();
            setError(data.data);
           }

        
    }
    if (error) {
        return <h1>Error: {error}</h1>;
      }
    
      
    return (
      <Row>
        <Col>
          
          <Card>
            <CardTitle tag="h6" className="border-bottom p-3 mb-0">
              <i className="bi bi-bell me-2"> </i>
              Add New student
            </CardTitle>
            <CardBody>
              <Form>
                <FormGroup>
                  <Label for=" ">Student name</Label>
                  <Input
                    id=" "
                    name="email"
                    placeholder="with a placeholder"
                    type="text"
                    value={name}
            onChange={(e) => setName(e.target.value)}
                  />
                </FormGroup>
                <FormGroup>
                  <Label for=" ">Age</Label>
                  <Input
                    id=" "
                    name="email"
                    placeholder="with a placeholder"
                    type="text"
                    value={age}
                    onChange={(e) => setAge(e.target.value)}
                  />
                </FormGroup>
                <FormGroup>
                  <Label for=" ">Address</Label>
                  <Input
                    id=" "
                    name="email"
                    placeholder="with a placeholder"
                    type="text"
                    value={address}
                    onChange={(e) => settaddress(e.target.value)}
                    />
                </FormGroup>
                <FormGroup>
                <div className="input-group mb-3">
  <div className="input-group-prepend">
    <label className="input-group-text" htmlFor="inputGroupSelect01">Gender</label>
  </div>
  <select className="custom-select" id="inputGroupSelect01"value={gender} onChange={(e) => setGender(e.target.value)}>
    <option  >--SELECT GENDER--</option>
    <option value="Male">Male</option>
    <option value="Female">Female</option>
   </select>
</div>
     
                </FormGroup>
                <FormGroup>
                <Label for=" ">Image</Label>
                <input
                  type="file"
                  accept="image/jpg, image/jpeg, image/png"
                  multiple
                  onChange={(e) => {
                    if (e.target.files && e.target.files.length > 0) {
                      setFiles(e.target.files);
                      setImage(e.target.files);
                    }
                  }}
                />
              </FormGroup>
              <div>
                {previews.map((preview, index) => (
                  <img key={index} src={preview} alt="Preview" style={{ width: '100px', height: '100px' }} />
                ))}
              </div>

           
                <button onClick={AddStudentToClass} className="btn btn-success" style={{ color: 'white', textDecoration: 'underline' }} type='button'>Add student</button>

              </Form>
            </CardBody>
          </Card>
        </Col>
      </Row>
    );
  };
  
  export default AddStudent;
